/**
 * Check if courses are already set up
 */
const areCoursesSetUp = async (): Promise<boolean> => {
  const { data, error } = await supabase
    .from('courses')
    .select('id')
    .limit(1);
    
  if (error) {
    console.error('Error checking if courses exist:', error);
    return false;
  }
  
  return data && data.length > 0;
};

